import React, { Component } from 'react'
import RobotList from './robotList';
import SearchBox from './SearchBox';
import './App.css'



class App extends Component {
    constructor() {
        super()
        this.state= {
            cartoes: [],
            searchfield: '' 
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response=> response.json())
            .then(users => this.setState({ cartoes: users }))
    }


    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value})


    }
    render () {
        const filteredcartoes = this.state.cartoes.filter(cartoes =>{
            return cartoes.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
        })
    return (
        <div className='tc'>
            <h1>Robots Friends</h1>
            <SearchBox searchChange={this.onSearchChange} />
            <RobotList  cartoes={filteredcartoes}/>
        </div>
    );
    }
}

export default App;
