import React, { Component } from 'react'
import RobotList from './robotList';
import SearchBox from './SearchBox';
import Scroll from './Scroll.js';
import './index.css';
import './App.css'




class App extends Component {
    constructor() {
        super()
        this.state= {
            cartoes: [],
            searchfield: '' 
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response=> response.json())
            .then(users => this.setState({ cartoes: users}))
    }


    onSearchChange = (event) => {
        this.setState({ searchfield: event.target.value})
    }

    render () {
        const filteredcartoes = this.state.cartoes.filter(cartoes =>{
            return cartoes.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
        })

        if (this.state.cartoes.length === 0) {
            return <h1>Loading</h1>
        } else {
            return (
                <div className='tc'>
                    <h1>Robots Friends</h1>
                    <SearchBox searchChange={this.onSearchChange} />
                    <Scroll>
                        <RobotList cartoes={filteredcartoes}/>
                    </Scroll>
                    <RobotList  cartoes={filteredcartoes}/>
                </div>
                );
            }
    }
}

export default App;
