import React, { Component } from 'react'
import {cartoes} from './cartoes';
import RobotList from './robotList';
import SearchBox from './SearchBox';

// const state = {
//     cartoes: cartoes,
//     searchfield: ''
// }

class App extends Component {
    constructor() {
        super()
        this.state= {
            cartoes: cartoes,
            searchfield: ''
        }
    }
    onSearchChange(event) {
        console.log(event.target.value);
        const filteredcartoes = this.state.cartoes.filter(cartoes =>{
            return cartoes.name.toLowerCase().includes(this.searchfield.toLowerCase());
        })

    }
    render () {
    return (
        <div className='tc'>
            <h1>Robots Friends</h1>
            <SearchBox searchChange={this.onSearchChange} />
            <RobotList  cartoes={this.state.cartoes}/>
        </div>
    );
    }
}

export default App;
